import React, { useEffect, useState } from "react";
import "./Meme.css";
import { useHistory } from "react-router-dom";
// import Footer from "../components/Footer";

export const Meme = () => {
  const [memes, setMemes] = useState([]);
  const [memeIndex, setMemeIndex] = useState(0);
  const [captions, setCaptions] = useState([]);

  const history = useHistory();

  const updateCaption = (e, index) => {
    const text = e.target.value || "";
    setCaptions(
      captions.map((c, i) => {
        if (index === i) {
          return text;
        } else {
          return c;
        }
      })
    );
  };

  const generateMeme = () => {
    const currentMeme = memes[memeIndex];
    const formData = new FormData();

    formData.append("username", "shashank4717"); // username from imgflip account
    formData.append("password", "Krishika@2601"); // password from imgflip account
    formData.append("template_id", currentMeme.id);
    captions.forEach((c, index) => formData.append(`boxes[${index}][text]`, c));

    fetch("https://api.imgflip.com/caption_image", {
      method: "POST",
      body: formData,
    }).then((res) => {
      res.json().then((res) => {
        history.push(`/generated?url=${res.data.url}`);
      });
    });
  };

  const shuffleMemes = (array) => {
    for (let i = array.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * i);
      const temp = array[i];
      array[i] = array[j];
      array[j] = temp;
    }
  };

  useEffect(() => {
    fetch("https://api.imgflip.com/get_memes").then((res) => {
      res.json().then((res) => {
        const _memes = res.data.memes;
        shuffleMemes(_memes);
        setMemes(_memes);
      });
    });
  }, []);

  useEffect(() => {
    if (memes.length) {
      setCaptions(Array(memes[memeIndex].box_count).fill(""));
    }
  }, [memeIndex, memes]);

  return memes.length ? (
    <div className="container">
      <div className="d-grid gap-2">
        <Button
          variant="primary"
          size="lg"
          onClick={this.onClick}
          style={{ margin: 14 }}
        >
          {" "}
          Generate Meme
        </Button>
        {""}

        <br />
        {showform && this.renderform()}

        <br />
        <div>
          {this.state.allMemeImgs.map((allmeme) => (
            <div style={{ display: "inline-grid" }}>
              <img src={allmeme.url} width={200} height={200} />
              <figcaption style={({ height: 70 }, { width: 210 })}>
                {allmeme.name}
                <input
                  name="r1"
                  type="radio"
                  checked={true === this.state.myValue}
                  style={{ color: "violet" }}
                  onChange={(e) => {
                    this.setState({ myValue: true });
                    this.setState({ randomImg1: allmeme.url });
                  }}
                />
              </figcaption>
            </div>
          ))}
        </div>
      </div>
    );

      <button onClick={generateMeme} className="generate">
        Generate!
      </button>
      <button onClick={() => setMemeIndex(memeIndex + 1)} className="skip">
        Skip
      </button>
      {captions.map((c, index) => (
        <input onChange={(e) => updateCaption(e, index)} key={index} />
      ))}
      <img alt="meme" src={memes[memeIndex].url} />
      {/* <Footer /> */}
    </div>
  ) : (
    <></>
  );
};
